import React, { useState } from "react";
import styled from "styled-components";
import Token from "./Token";
import Button from "./Button";
const TableStyled = styled.div`
  display: grid;
  grid-template-columns: 130px 130px;
  justify-content: center;
  justify-items: center;
  grid-gap: 30px 50px;
  margin: 2em 0;
  position: relative;
  & div:nth-of-type(3) {
    grid-column: span 2;
  }
  .in-game {
    text-transform: uppercase;
    text-align: center;
    font-size: 0.8em;
    font-weight: 700;
    letter-spacing: 1px;
  }
  .line {
    display: ${({ player }) => (!player ? "block" : "none")};
    height: 14px;
    background: rgba(0, 0, 0, 0.2);
    position: absolute;
    width: 200px;
    top: 60px;
    &:before {
      content: "";
      height: 14px;
      background: rgba(0, 0, 0, 0.2);
      position: absolute;
      left: 0px;
      right: 0px;
      top: 0px;
      transform: rotate(60deg);
      transform-origin: left top;
    }
    &:after {
      content: "";
      height: 14px;
      background: rgba(0, 0, 0, 0.2);
      position: absolute;
      left: 0px;
      right: 0px;
      top: 0px;
      transform: rotate(-60deg);
      transform-origin: right top;
    }
  }
`;

const elements = ["paper", "scissors", "rock"];

export default function Table() {
  const [player, setUserActive] = useState(false);
  const [pick, setPick] = useState("");

  function getRandomInit(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
  }

  function onClick(name) {
    console.log(elements[getRandomInit(0, 3)]);
    setUserActive(true);
    setPick(name);
  }

  function handleTryAgainClick(){
      setUserActive(false);
  }
  return (
    <TableStyled player={player}>
      <span className="line"></span>
      {!player ? (
        <React.Fragment>
          <Token name="paper" onClick={onClick} />
          <Token name="scissors" onClick={onClick} />
          <Token name="rock" onClick={onClick} />
        </React.Fragment>
      ) : (
        <>
          <div className="in-game">
            <Token name={pick} />
            <p> You Picked</p>
          </div>
          <div className="in-game">
            <Token />
            <p> The House Picked</p>
          </div>
          <div className="results">
            {" "}
            <h2> YOU ???</h2>{" "}
            <Button  onClick={handleTryAgainClick}> TRY AGAIN </Button> 
          </div>
        </>
      )}
    </TableStyled>
  );
}
