import React from "react";
import styled from "styled-components";
const TokenStyled = styled.div`
  width: 130px;
  height: 125px;
  border: 15px solid ${(props) => props.color.base};
  box-sizing: border-box;
  border-radius: 50%;
  display: flex;
  background: white;
  box-shadow: 0 5px 0px ${(props) => props.color.border};
  cursor: pointer;
  position: relative; 
  z-index: 2;
  &:active {
    transform: scale(0.9);
  }
  .box {
    display: flex;
    justify-content: center;
    align-items: center;
    box-shadow: 0 -4px 0 #BABFD4;
    flex: 1;
    align-self: stretch;
    border-radius: 50%;
  }
`;

const colors = {
  paper: {
    base: "#516ef4",
    border: "#2543c3",
  },
  rock: {
    base: "#de3a5a",
    border: "#980e31",
  },
  scissors: {
    base: "#eca81e",
    border: "#c76c14",
  },
  default: {
    base: "#122343",
    border: "#122343",
  }
};

export default function Token({ name='default',onClick }) {


  function handledClick(){
   onClick(name);

  }
  const color = colors[name]
  return (
    <TokenStyled onClick={handledClick} color={color}>
      <div className="box">
        <img src={`./images/icon-${name}.svg`} alt="" />
      </div>
    </TokenStyled>
  );
}
